import styled from "styled-components";
export const ContainerShirts = styled.aside`
  display: flex;
  width: ${(prop) => (prop.width ? prop.width : "100%")};
  button {
    z-index: 2;
    padding: 0px;
    height: 55px;
    display: flex;
    width: 100%;
    text-align: center;
    justify-content: center;
    cursor: pointer;
    position: relative;
    background-color: ${(prop) => (prop.bgColor ? prop.bgColor : "#09ac5d")};
    border-radius: ${(prop) => (prop.radius ? prop.radius : "10px")};
    border: none;
    font-size: ${(prop) => (prop.fontSize ? prop.fontSize : "18px")};
    box-shadow: 0px 0px 5px #b9b9b9e8;
    margin: 5px;
    & :active {
      border-radius: ${(prop) => (prop.radius ? prop.radius : "10px")};
      background-color: ${(prop) =>
        prop.bgColorActive ? prop.bgColorActive : "#01552c"};
    }
    p {
      color: #fff;
      font-weight: 900;
      display: flex;
      padding: 0px 15px 0px 15px;
      justify-content: center;
      align-items: center;
      margin: 0px;
      width: 100%;
      height: 100%;
      white-space: nowrap;
    }
  }
`;
