import { useState } from "react";
import { ContainerShirts } from "./style";
import WeeklyModal from "../../Modal";

import data from "./data.json";

export default function ButtonSimple({
  title,
  fontSize,
  width,
  bgColor,
  bgColorActive,
  radius,
  colorFont,
  colorFontActive,
  height,
  padding,
  shadow,
  borderColor,
}) {
  const [isOpenPopUp, setIsOpenPopUp] = useState(false);
  function onOpenPopUp() {
    setIsOpenPopUp(!isOpenPopUp);
  }

  const handleClose = () => setIsOpenPopUp(false);

  return (
    <>
      <ContainerShirts
        bgColor={bgColor}
        radius={radius}
        fontSize={fontSize}
        width={width}
        bgColorActive={bgColorActive}
        colorFont={colorFont}
        colorFontActive={colorFontActive}
        height={height}
        padding={padding}
        shadow={shadow}
        borderColor={borderColor}
      >
        <button
          onClick={() => {
            onOpenPopUp(true);
          }}
        >
          {data.map((data) => (
            <p>{data.style} </p>
          ))}
        </button>
      </ContainerShirts>

      <WeeklyModal
        handleClose={handleClose}
        open={isOpenPopUp}
        packages={`# Com npm > npm install styled-components 
