import styled from "styled-components";

export const ContainerModal = styled.aside`
  display: flex;
  position: absolute;
  overflow: hidden;
  width: 100vw;
  height: 100vh;
  z-index: 3;
  justify-content: center;
  align-items: center;
  .contentModal {
    position: relative;
    z-index: 2;
    height: auto;
    width: 80%;
    display: flex;
    text-align: center;
    justify-content: center;
    border: none;
    background-color: #232231;
    padding: 18px;
    border: solid 2px #09ac5d;
    border-radius: 10px;
    flex-direction: column;
    .btnClose {
      position: flex;
      background-color: #09ac5d;
      padding: 10px 20px;
      width: fit-content;
      border: none;
      border-radius: 5px;
      font-size: 20px;
      color: #fff;
      font-weight: 600;
      box-shadow: 0px 0px 5px #0000001f;
      cursor: pointer;
    }
  }
`;
