import { useState } from "react";
import { ContainerShirts } from "./style";
import WeeklyModal from "../../Modal";

export default function ButtonSimple({
  title,
  fontSize,
  width,
  bgColor,
  bgColorActive,
  radius,
  colorFont,
  onClick,
  colorFontActive,
  height,
  padding,
  shadow,
}) {
  const [isOpenPopUp, setIsOpenPopUp] = useState(false);
  function onOpenPopUp() {
    setIsOpenPopUp(!isOpenPopUp);
  }

  const handleClose = () => setIsOpenPopUp(false);

  return (
    <>
      <WeeklyModal handleClose={handleClose} open={isOpenPopUp} />
      <ContainerShirts
        bgColor={bgColor}
        radius={radius}
        fontSize={fontSize}
        width={width}
        bgColorActive={bgColorActive}
        colorFont={colorFont}
        colorFontActive={colorFontActive}
        height={height}
        padding={padding}
        shadow={shadow}
      >
        <button
          onClick={() => {
            onOpenPopUp(true);
          }}
        >
          <p>{title} </p>
        </button>
      </ContainerShirts>
    </>
  );
}
