import * as S from "./styles";
import { CopyAnimationIndex, CopyAnimationStyle } from "./data";
import ButtonSimple from "../../buttons/buttonSimple";
import { useState } from "react";

export default function Animation01() {
  const [isAnimation, setIsAnimation] = useState(false);
  function onAnimation() {
    setIsAnimation(!isAnimation);
  }
  const [isOpenCode, setIsOpenCode] = useState(false);
  function onOpenCode() {
    setIsOpenCode(!isOpenCode);
  }
  const [isCodeView, setIsCodeView] = useState(0);

  return (
    <S.Container>
      <pre>
        <S.Animation>
          <ButtonSimple
            width={"200px"}
            title={"Animar"}
            onClick={() => {
              onAnimation(true);
            }}
          />
          {isAnimation && <p className="text">Animação 01</p>}
        </S.Animation>
        <S.CodeView>
          <div className="viewCode">
            <div>
              <span>
                <p
                  onClick={() => {
                    onOpenCode(true);
                  }}
                  className={(isOpenCode && "codeOpen") || "codeClose"}
                >
                  Ver codigo
                </p>
              </span>
              {isOpenCode && (
                <span>
                  <p onClick={() => setIsCodeView === 0}>Index.jsx</p>
                  <p onClick={() => setIsCodeView === 1}>Style.jsx</p>
                </span>
              )}
            </div>
          </div>
          {isOpenCode && (
            <div>
              {setIsCodeView === 0 && <CopyAnimationIndex />}
              {setIsCodeView === 1 && <CopyAnimationStyle />}
            </div>
          )}
        </S.CodeView>
      </pre>
    </S.Container>
  );
}
