import * as S from "./styles";

import hljs from "highlight.js";
import "highlight.js/styles/base16/dracula.css";
import { useEffect } from "react";
import { marked } from "marked";

const themeColors = `
  \`\`\`
  // Cores no arquivo theme.jsx                                

  export const colors = {
    primaryOrange: "#F28E74",
    primaryGreen: " #7EC3C0",
    segundaryGreen: "#0D827F",
    background: "#191923",
    dark: "#2C2C2C",
};
  \`\`\`
`;

const copyStyle = `
  \`\`\`
  // Cores no arquivo theme.jsx                                

  export const colors = {
    primaryOrange: "#F28E74",
    primaryGreen: " #7EC3C0",
    segundaryGreen: "#0D827F",
    background: "#191923",
    dark: "#2C2C2C",
};
import styled from "styled-components";
import { colors } from "../../theme";


  \`\`\`
`;

export default function ThemePage() {
  useEffect(() => {
    hljs.highlightAll();
  });
  return (
    <S.Container>
      <h1>Usando theme</h1>
      <section>
        <pre>
          <S.Content>
            <p>
              <span />
              primaryOrange
              <span />
              primaryGreen
              <span />
              segundaryGreen
              <span />
              background
              <span />
              dark
            </p>
            <div
              dangerouslySetInnerHTML={{ __html: marked(themeColors) }}
            ></div>
          </S.Content>
        </pre>
        <pre>
          <S.Content>
            <div dangerouslySetInnerHTML={{ __html: marked(copyStyle) }}></div>
          </S.Content>
        </pre>
      </section>
    </S.Container>
  );
}
