import { CopyStyle, CopyIndex, CopyComponent } from "./data";
import ButtonSimple from "../../components/buttons/buttonSimple";
import { useState } from "react";

export default function Animations() {
  const [isAnimation01, setIsAnimation01] = useState(false);
  function onAnimation01() {
    setIsAnimation01(!isAnimation01);
  }

  return (
    <S.Container>
      <section className="boxAll">
        <h1>Animações</h1>
        <section>
          <S.Content>
            <pre>
              {/*Animação 01*/}
              <section className="animation">
                <ButtonSimple
                  width={"200px"}
                  title={"Animar"}
                  onClick={() => {
                    onAnimation01(true);
                  }}
                />
                {isAnimation01 && (
                  <p className="animationExemple01">Animação 01</p>
                )}
              </section>
              <p>index.jsx</p>
              <CopyIndex />
            </pre>
          </S.Content>
        </section>
      </section>
    </S.Container>
  );
}
