import styled from "styled-components";

export const Container = styled.aside`
  justify-items: center;
  align-items: center;
  height: 100vh;
  width: 100%;
  flex-direction: column;
  background-color: #191923;
  display: flex;
  section {
    display: flex;
    gap: 30px;
    flex-direction: row;
    pre {
      p {
        display: flex;
        flex-direction: row;
        span {
        }
      }
      code {
        border-radius: 10px;
      }
    }
  }
  h1 {
    color: #15e84c;
    font-size: 50px;
    font-family: "Courier New", Courier, monospace;
  }
`;
