import TextField from "@mui/material/TextField";
import { styled } from "@mui/material/styles";
import * as S from "./styles";

const CssTextField = styled(TextField)({
  width: "100%",
  "& label.Mui-focused": {
    background: "#fff",
    padding: "0px 10px 0px 10px",
    color: "#7EC3C0",
    marginTop: "0px",
  },
  "& label": {
    color: "#A0AAB4",
    background: "#fff",
    padding: "0px 10px 0px 10px",
  },
  "& input": {
    height: "20px",
    paddingLeft: "20px",
  },
  "& .MuiInput-underline:after": {
    borderBottomColor: "#B2BAC2",
  },
  "& .MuiOutlinedInput-root": {
    "& fieldset": {
      borderColor: "#E0E3E7",
    },
    "&:hover fieldset": {
      borderColor: "#B2BAC2",
    },
    "&.Mui-focused fieldset": {
      borderColor: "#7EC3C0",
    },
  },
});

export default function Input({ name, label, error, required, type }) {
  return (
    <S.Container>
      <CssTextField
        name={name}
        label={label}
        id={name}
        aria-errormessage="Valor invalido"
        helperText={error ? "Valor invalido" : ""}
        required={required}
        type={type}
        error={error}
      />
    </S.Container>
  );
}
