import * as S from "./styles";

import hljs from "highlight.js";
import "highlight.js/styles/base16/dracula.css";
import { useEffect } from "react";
import { marked } from "marked";

const themeColors = `
  \`\`\`
  // Cores no arquivo theme.jsx                                

  export const colors = {
    primaryOrange: "#F28E74",
    primaryGreen: " #7EC3C0",
    segundaryGreen: "#0D827F",
    background: "#191923",
    dark: "#2C2C2C",
};
  \`\`\`
`;

const copyStyle = `
  \`\`\`
  // Cores no arquivo theme.jsx                                

  import styled from "styled-components";
import { colors } from "../../theme";

export const Content = styled.div'
  p {
    display: flex;
    flex-direction: row;
    color: #c5c5c5;
    align-items: center;
    margin: 0px;
    span {
      display: block;
      width: 15px;
      height: 15px;
      margin-right: 5px;
      border-radius: 100%;
      margin-bottom: -2px;
      border: solid 0.5px #ffffff63;
    }
    span:nth-of-type(1) {
      background-color: ${"colors.primaryOrange"};
    }
    span:nth-of-type(2) {
      background-color: ${"colors.primaryGreen"};
      margin-left: 10px;
    }
    span:nth-of-type(3) {
      background-color: ${"colors.segundaryGreen"};
      margin-left: 10px;
    }
    span:nth-of-type(4) {
      background-color: ${"colors.background"};
      margin-left: 10px;
    }
    span:nth-of-type(5) {
      'background-color: ${colors.dark}'
      margin-left: 10px;
    }
  }
';
  \`\`\`
`;

export default function ThemePage() {
  useEffect(() => {
    hljs.highlightAll();
  });
  return (
    <S.Container>
      <h1>Usando theme</h1>
      <section>
        <pre>
          <S.Content>
            <p>
              <span />
              primaryOrange
              <span />
              primaryGreen
              <span />
              segundaryGreen
              <span />
              background
              <span />
              dark
            </p>
            <div
              dangerouslySetInnerHTML={{ __html: marked(themeColors) }}
            ></div>
          </S.Content>
        </pre>
        <pre>
          <S.Content>
            <div dangerouslySetInnerHTML={{ __html: marked(copyStyle) }}></div>
          </S.Content>
        </pre>
      </section>
    </S.Container>
  );
}
