import { useState } from "react";
import * as S from "./style";
import { CopyPackages, CopyIndex, CopyStile, Obs } from "./data.jsx";

export default function ButtonSimple({
  title,
  fontSize,
  width,
  bgColor,
  bgColorActive,
  radius,
  colorFont,
  colorFontActive,
  height,
  padding,
  shadow,
  borderColor,
  borderWidth,
}) {
  const [isOpenPopUp, setIsOpenPopUp] = useState(false);
  function onOpenPopUp() {
    setIsOpenPopUp(!isOpenPopUp);
  }
  const handleClose = () => setIsOpenPopUp(false);

  return (
    <>
      <S.ContainerShirts
        bgColor={bgColor}
        radius={radius}
        fontSize={fontSize}
        width={width}
        bgColorActive={bgColorActive}
        colorFont={colorFont}
        colorFontActive={colorFontActive}
        height={height}
        padding={padding}
        shadow={shadow}
        borderColor={borderColor}
      >
        <button
          onClick={() => {
            onOpenPopUp(true);
          }}
        >
          <p>{title}</p>
        </button>
      </S.ContainerShirts>

      <S.Container>
        <section>
          <S.Content>
            <pre>
              <code>
                <p>Index.jsx</p>
                <CopyPackages />
              </code>
            </pre>
            <pre>
              <p>index.jsx</p>
              <CopyIndex />
            </pre>
          </S.Content>

          <S.ContentSize>
            <pre>
              <p>
                <span />
                xs
                <span />s
                <span />m
                <span />l
                <span />
                xl
                <span />
                xxl
              </p>
              <CopyStile />
            </pre>
          </S.ContentSize>
        </section>
      </S.Container>
    </>
  );
}
