import * as S from "./styles";

import hljs from "highlight.js";
import "highlight.js/styles/base16/dracula.css";
import { useEffect } from "react";
import { marked } from "marked";

const themeColors = `
  \`\`\`
  // Cores no arquivo theme.jsx                                

  export const colors = {
    primaryOrange: "#F28E74",
    primaryGreen: " #7EC3C0",
    segundaryGreen: "#0D827F",
    background: "#191923",
    dark: "#2C2C2C",
};
  \`\`\`
`;

const copyStyle = `
  \`\`\`
import styled from "styled-components";
import { colors } from "../../theme";

export const Content = styled.div\`
  p {
    display: flex;
    color: #c5c5c5;
    margin: 0px;
    span {
      display: block;
      width: 15px;
      height: 15px;
      margin-right: 8px;
      border-radius: 100%;
      border: solid 0.5px #ffffff63;
      margin-left: 15px;
    }
    span:nth-of-type(1) {
      background-color: ${"colors.primaryOrange"};
      margin-left: 0px;
    }
    span:nth-of-type(2) {
      background-color: ${"colors.primaryGreen"};
    }
    span:nth-of-type(3) {
      background-color: ${"colors.segundaryGreen"};
    }
    span:nth-of-type(4) {
      background-color: ${"colors.background"};
    }
    span:nth-of-type(5) {
      background-color: ${"colors.dark"};
    }
  }
\`
  \`\`\`
`;

export default function ThemePage() {
  useEffect(() => {
    hljs.highlightAll();
  });
  return (
    <S.Container>
      <h1>Usando theme</h1>
      <section>
        <pre>
          <S.Content>
            <p>
              <span />
              primaryOrange
              <span />
              primaryGreen
              <span />
              segundaryGreen
              <span />
              background
              <span />
              dark
            </p>
            <div
              dangerouslySetInnerHTML={{ __html: marked(themeColors) }}
            ></div>
          </S.Content>
        </pre>
        <pre>
          <S.ContentSize>
            <p>
              <span>xs</span>
              <span>s</span>
              <span>m</span>
              <span>l</span>
              <span>xl</span>
              <span>xxl</span>
            </p>
            <div
              dangerouslySetInnerHTML={{ __html: marked(themeColors) }}
            ></div>
          </S.ContentSize>
        </pre>
        <pre>
          <S.Content>
            <p>style.jsx</p>
            <div dangerouslySetInnerHTML={{ __html: marked(copyStyle) }}></div>
          </S.Content>
        </pre>
      </section>
    </S.Container>
  );
}
