import { useNavigate } from "react-router-dom";
import { ContainerShirts } from "./style";
import CircularProgress from "@mui/material/CircularProgress";

function Button({
  title,
  fontSize,
  width,
  bgColor,
  type,
  loading,
  radius,
  onClick,
  href,
  to,
}) {
  const navigate = useNavigate();
  function handleClick() {
    if (to) {
      navigate(to);
      return;
    }

    if (href) {
      window.open(href, "_blank", "noopener noreferrer");
      return;
    }

    if (onClick) {
      onClick();
      return;
    }
  }
  return (
    <ContainerShirts bgColor={bgColor} radius={radius} fontSize={fontSize}>
      <button
        className="btn"
        style={{ width: width }}
        type={type}
        disabled={loading}
        onClick={handleClick}
      >
        <p className="text">
          {loading ? <CircularProgress size={20} color="inherit" /> : title}
        </p>
      </button>
    </ContainerShirts>
  );
}

export default Button;
