import styled from "styled-components";

import { colors } from "../../theme";

export const Container = styled.aside`
  justify-items: center;
  align-items: center;
  height: 100vh;
  width: 100%;
  flex-direction: column;
  background-color: #191923;
  display: flex;
  section {
    display: flex;
    gap: 30px;
    flex-direction: row;
    pre {
      p {
        display: flex;
        flex-direction: row;
        color: #c5c5c5;
        align-items: center;
        margin: 0px;
        span {
          display: block;
          width: 15px;
          height: 15px;
          margin-right: 5px;
          border-radius: 100%;
          margin-bottom: -2px;
          border: solid 0.5px #ffffff63;
        }
        span:nth-of-type(1) {
          background: #f28e74;
        }
        span:nth-of-type(2) {
          background: #7ec3c0;
          margin-left: 10px;
        }
        span:nth-of-type(3) {
          background: #0d827f;
          margin-left: 10px;
        }
        span:nth-of-type(4) {
          background-color: ${colors.background};
          margin-left: 10px;
        }
        span:nth-of-type(5) {
          background-color: ${colors.dark};
          margin-left: 10px;
        }
      }
      code {
        border-radius: 10px;
      }
    }
  }
  h1 {
    color: #15e84c;
    font-size: 50px;
    font-family: "Courier New", Courier, monospace;
  }
`;
