import styled from "styled-components";
import { colors } from "../../theme";
export const ContainerShirts = styled.aside`
  display: flex;
  .btn {
    z-index: 2;
    width: calc(100% - 10px);
    padding: 0px;
    height: 55px;
    display: flex;
    text-align: center;
    justify-content: center;
    cursor: pointer;
    position: relative;
    background-color: ${(prop) => (prop.bgColor ? prop.bgColor : red)};
    border-radius: ${(prop) => (prop.radius ? prop.radius : "50px")};
    border: none;
    font-size: ${(prop) => (prop.fontSize ? prop.fontSize : "18px")};
    box-shadow: 0px 0px 5px #b9b9b9e8;
    margin: 5px;
    & :active {
      border-radius: ${(prop) => (prop.radius ? prop.radius : "50px")};
      background-color: ${(prop) => (prop.bgColor ? prop.bgColor : red)};
    }

    .text {
      color: #fff;
      font-weight: 900;
      margin: 0px !important;
      line-height: 15px !important;
      display: flex;
      padding: 0px 15px 0px 15px;
      justify-content: center;
      align-items: center;
      margin: 0px;
      width: 100%;
      height: 100%;
      white-space: nowrap;
    }
  }
`;
