import styled from "styled-components";

export const Container = styled.aside`
  background-color: transparent;
  height: auto;
  width: 100%;
  position: relative;
  display: flex;
  padding: 0px 0px 30px 0px;
  & input {
    width: calc(100% - 16px);
  }
`;
